import { useEffect } from 'react';
import './Abilities.sass'
import Glide from '@glidejs/glide'
import '@glidejs/glide/dist/css/glide.core.min.css';



interface SimpleSliderProps {
    children: React.ReactNode;
    glideClass: string;
    config: object
}

export function SimpleSlider( {children, glideClass, config}: SimpleSliderProps ) {

      useEffect(() => {
        const glide = new Glide(`.glide.${glideClass}`,{
            type: 'slider',
            ...config
        });
        glide.mount();
    
        // Cleanup function to destroy Glide instance
        return () => {
          glide.destroy();
        };
      }, []);

      
    return (
        <>
            <div className={"glide " + glideClass}>
            <div className="glide__track" data-glide-el="track">
                <ul className="glide__slides">
                    {children}
                </ul>
            </div>
            </div>
            
        </>
    );
  }

